mapper.select:
select rn,bid,btitle,bcontent,battachfile,bdate,vid	
	from(
	    select rownum as rn, bid, btitle, bcontent, battachfile, bdate, vid
	    from(
	        select bid,btitle,bcontent,battachfile,bdate,vid	
	        from board
	    )
	    where rownum <= #{endRowNo}
	)
	where rn > = #{startRowNo}
